<?php  require_once 'config/config.php';require_once 'class_help.php';class ConectarPostgre extends Postgres{ public $result=array(); public $consulta; protected function verificador($consulta){if($consulta>0){return true;}else{return false;}} public function InsertRegistro($sql){$this->consulta=pg_query($sql) or die('Fatal Error: '.pg_last_error());return self::verificador($this->consulta);} protected function contador($consulta){$contador=pg_num_rows($consulta);return $contador;} public function SelectRegistro($sql){$this->consulta=pg_query($sql) or die('Fatal Error: '.pg_last_error());if(self::contador($this->consulta)){return true;}else{return false;}} public function ListRegistro(){while($res=pg_fetch_assoc($this->consulta)){$this->result[]=$res;}return $this->result;} public function UpdateRegistro($sql){$this->consulta=pg_query($sql) or die('Fatal Error: '.pg_last_error());return self::verificador($this->consulta);} public function DeleteRegistro($sql){$this->consulta=pg_query($sql) or die('Fatal Error: '.pg_last_error());return self::verificador($this->consulta);}}